    event TokenDeposited(
        address _token,
        uint256 _tokenMarketIndex,
        uint256 _amount,
        uint256 _liquidityMinted
    );

    event LiquidityMoved(
        address _dHedgeCore,
        uint256 _amount,
        uint256 _timestamp
    );

    event LiquidityWithdrawn(
        address _dHedgeCore,
        address _user,
        uint256 _amount,
        uint256 _timestamp
    );

