        external
        view
        returns (bool _canExec, bytes memory _execPayload);
}

contracts/dHedge/dHedgeUpkeepGelato.sol.content:
// SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.4;

import {IdHedgeCore, IdHedgeUpkeep} from "./Interfaces/ISuperdHedge.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";

/**
 * @title dHedge upkeep contract
 * @author rashtrakoff
 * @dev Resolver contract for keepers
 * @custom:experimental This is an experimental contract/library. Use at your own risk.
 */
// solhint-disable not-rely-on-time
// solhint-disable reason-string
// solhint-disable-next-line contract-name-camelcase
contract dHedgeUpkeepGelato is Ownable, IdHedgeUpkeep {
    // Mapping to represent upkeep status of a contract (true -> ongoing/unpaused, false -> paused)
    mapping(address => bool) private upkeepSet;

    /// @dev Pauses a core contract from upkeep services
    /// @param _contract Address of the core contract
    function pauseContract(address _contract) external {
        _onlyOwner(msg.sender);
        require(upkeepSet[_contract], "dHedgeUpkeep: Contract already paused");

        upkeepSet[_contract] = false;

        emit CorePaused(_contract);
    }

    /// @dev Unpauses a core contract from upkeep services
    /// @param _contract Address of the core contract
    /// Unpausing and adding a contract are equivalent actions in our case
    function unPauseContract(address _contract) external {
        _onlyOwner(msg.sender);
        require(
            !upkeepSet[_contract],
            "dHedgeUpkeep: Contract already unpaused"
        );

        upkeepSet[_contract] = true;

        emit CoreUnPaused(_contract);
    }

    /// @dev Calls deposit function in a core contract. Should be used by keepers.
    /// @param _contract Address of the core contract
    /// @param _depositToken Address of the token to be deposited
    function performUpkeep(address _contract, address _depositToken)
        external
        override
    {
        try IdHedgeCore(_contract).dHedgeDeposit(_depositToken) {
            emit DepositSuccess(_contract, _depositToken);
        } catch (bytes memory _err) {
            // Pause the contract until the issue is manually resolved
            upkeepSet[_contract] = false;

            emit DepositFailed(_contract, _depositToken, _err);
        }
    }

    /// @dev Function which checks if any of the registered core contracts require upkeep
    function checkUpkeep(address _contract)
        external
        view
        override
        returns (bool _canExec, bytes memory _execPayload)
    {
        if (upkeepSet[_contract]) {
            try IdHedgeCore(_contract).requireUpkeep() returns (
                bool _result,
                address _token
            ) {
                _canExec = _result;

                _execPayload = abi.encodeWithSelector(
                    IdHedgeUpkeep.performUpkeep.selector,
                    _contract,
                    _token
                );
            } catch (bytes memory error) {
                // To allow debugging using Tenderly dashboard
                console.logBytes(error);
            }
        }
    }

    function _onlyOwner(address _user) internal view {
        require(_user == owner(), "dHedgeUpkeepGelato: Not the owner");
    }
}

contracts/dHedge/dHedgeBank.sol.content:
// SPDX-License-Identifier: Unlicensed
pragma solidity ^0.8.4;

import {IdHedgeBank} from "./Interfaces/ISuperdHedge.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// import "hardhat/console.sol";

/**
 * @title dHedge bank contract
 * @author rashtrakoff
 * @dev Accounts for LP tokens received by different core contracts
 * @custom:experimental This is an experimental contract/library. Use at your own risk.
 */
// solhint-disable reason-string
// solhint-disable-next-line contract-name-camelcase
contract dHedgeBank is Ownable, IdHedgeBank {
    using SafeERC20 for IERC20;

    mapping(address => mapping(address => uint256)) private depositContracts;

    /// @dev Function called by core contracts for depositing their LP tokens
    /// @param _poolToken Address of the dHedge pool
    /// @param _amount Amount of LP tokens to be deposited
    function deposit(address _poolToken, uint256 _amount) external override {
        require(
            _isContract(msg.sender),
            "dHedgeBank: Depositor not a contract"
        );
        require(
            Ownable(msg.sender).owner() == owner(),
            "dHedgeBank: Owner of deposit contract not authorised"
        );

        IERC20(_poolToken).safeTransferFrom(msg.sender, address(this), _amount);
        depositContracts[msg.sender][_poolToken] += _amount;
    }

    /// @dev Function called by core contracts to transfer LP tokens to investors
    /// @param _poolToken Address of the dHedge pool
    /// @param _user Address of the user to whom LP tokens are to be transferred
    /// @param _amount Amount of tokens to be transferred to the user
    function withdraw(
        address _poolToken,
        address _user,
        uint256 _amount
    ) external override {
        require(
            _isContract(msg.sender),
            "dHedgeBank: Withdrawer not a contract"
        );
        require(
            Ownable(msg.sender).owner() == owner(),
            "dHedgeBank: Owner of deposit contract not authorised"
        );
        require(
            depositContracts[msg.sender][_poolToken] >= _amount,
            "dHedgeBank: Amount exceeds limit"
        );

        depositContracts[msg.sender][_poolToken] -= _amount;
        IERC20(_poolToken).safeTransfer(_user, _amount);
    }

    /// @dev Function to withdraw a token in case of emergency
    /// @param _token Address of the pool token
    /// Remove/Modify this function after testing
    function emergencyWithdraw(address _token) external onlyOwner {
        IERC20(_token).safeTransfer(
            owner(),
            IERC20(_token).balanceOf(address(this))
        );
    }

    /// @dev Checks if a given address is a contract or not
    /// @param account Address of the account
    function _isContract(address account) internal view returns (bool) {
        // This method relies on extcodesize, which returns 0 for contracts in
        // construction, since the code is only stored at the end of the
        // constructor execution.

        /* solhint-disable no-inline-assembly */
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
        /* solhint-enable no-inline-assembly */
    }
}
